import { mapActions, mapGetters } from "vuex";

export default {
  computed: {
    ...mapGetters({
      favorites: "houseModule/getFavorites",
    }),
  },
  methods: {
    ...mapActions({
      addToFavorites: "houseModule/addToFavorites",
      removeFromFavorites: "houseModule/removeFromFavorites",
    }),
    toggleFavorite(house) {
      if (this.isFavorite(house)) {
        this.removeFromFavorites(house.id);
      } else {
        this.addToFavorites(house);
      }
    },
    isFavorite(house) {
      return this.favorites.some((h) => h.id === house.id);
    },
  },
};
